    [59569] = { 31717, 1, 128, "青铜幼龙", "\"失去的时间永远无法挽回。 哎哟! 在这里，我找到了...\" – 克罗诺姆", 0, "|cFFFFD200掉落： |r永恒腐蚀者|n|cFFFFD200地区：|r净化斯坦索姆" },
    [59570] = { 31697, 1, 128, "红色幼龙", "吟游诗人所唱的一切都是愚蠢的小说。 除非它首先受到攻击，否则龙不会伤害苍蝇。", 2, "|cFFFFD200商人出售：|r希尔丝塔萨|n|cFFFFD200地区：|r龙骨荒野|n|cFFFFD200阵营：|r龙眠联军- 崇拜|n|cFFFFD200价格：|r2000|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [59571] = { 31698, 1, 128, "暮光幼龙", "但最重要的规则，无论他怎么哭，无论他怎么乞求，在任何情况下都不应忘记，永远不要在午夜之后喂他。", 0, "|cFFFFD200掉落：|r 萨塔里奥|n|cFFFFD200地区：|r 黑曜石圣殿" },
    [59572] = { 31699, 2, 64, "黑色北极熊", "牦牛人在他们的宗教仪式中使用黑色北极熊毛皮，通常是在那些猎杀北极熊的人的葬礼上。", 2, "|cFFFFD200传家宝|r" },
    [59573] = { 31700, 2, },
    [59650] = { 31778, 1, 128, "黑色幼龙", "世界上没有什么事情值得惹恼一条活生生的龙。", 0, "|cFFFFD200掉落：|r 萨塔里奥" },
    [59785] = { 31849, 2, 4, "黑色猛犸战象", "在野外，这些聪明的动物表现得非常冷静，但当它们背上鞍时，它们会与骑手建立特殊的联系，无所畏惧地投入战斗。", 2, "|cFFFFD200商人出售：|r骑士达米隆|n|cFFFFD200地区：|r冬拥湖|n|cFFFFD200价格：|r300|Hitem:43228|h|TInterface\\ICONS\\INV_Misc_Platnumdisks:0|t|h" },
    [59788] = { 31850, 2, 4, "黑色猛犸战象", "在战斗中坚定的步伐和从容不迫的冷静——这就是这些黑皮肤猛犸象所看重的。", 2, "|cFFFFD200商人出售：|r石头守卫穆卡尔|n|cFFFFD200地区：|r冬拥湖|n|cFFFFD200价格：|r300|Hitem:43228|h|TInterface\\ICONS\\INV_Misc_Platnumdisks:0|t|h" },
    [59791] = { 31851, 2, 4, "长毛猛犸象", "长毛猛犸象是其物种远古祖先的直系后代。 他们比他们的短发亲戚更接近大自然。", 2, "|cFFFFD200商人出售：|r梅尔·弗兰希斯|n|cFFFFD200地区：|r达拉然|n|cFFFFD200价格：|r200|TINTERFACE\\ICONS\\Spell_Holy_ProclaimChampion:0|t" },
    [59793] = { 31852, 2, 4, "长毛猛犸象", "这种野生物种不是为骑马而饲养的。 大多数长毛猛犸象都会落入野外猎人的陷阱。 他们的凶猛和狡猾是驯服和训练的结果。", 2, "|cFFFFD200商人出售：|r梅尔·弗兰希斯|n|cFFFFD200地区：|r达拉然|n|cFFFFD200价格：|r200|TINTERFACE\\ICONS\\Spell_Holy_ProclaimChampion:0|t" },
    [59797] = { 31854, 2, 4, "冰雪猛犸象", "霍迪尔的儿子们相信冰猛犸象骑手不怕冻伤和雪盲。", 2, "|cFFFFD200商人出售：|r李奥霍夫|n|cFFFFD200地区：|r风暴峭壁|n|cFFFFD200阵营：|r霍迪尔之子 - 崇敬|n|cFFFFD200价格：|r1000|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [59799] = { 31855, 2, 4, "冰雪猛犸象", "许多冰巨人声称已经教他们的宠物如何吹喇叭，以便雪崩从山上下来。", 2, "|cFFFFD200商人出售：|r李奥霍夫|n|cFFFFD200地区：|r风暴峭壁|n|cFFFFD200阵营：|r霍迪尔之子 - 崇敬|n|cFFFFD200价格：|r1000|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
