    [59976] = { 31912, 1, 192, "黑色始祖幼龙", "这个品种是专门为纪念在诺森德表现出非凡韧性和坚韧的英雄而培育的。", 2, "|cFFFFD200传家宝|r" },
    [59996] = { 32151, 1, 128, "蓝色始祖幼龙", "\"风暴峭壁! 在那里，我们会找到一头足以让我骑上战场的野兽。\", – 残忍的斯卡迪", 0, "|cFFFFD200掉落： |r残忍的斯卡迪|n|cFFFFD200地区：|r乌特加德之巅" },
    [60002] = { 32153, 1, 128, "迷失始祖幼龙", "时间存在的唯一原因是一切都不会同时发生。", 0, "|cFFFFD200掉落： |r迷失始祖幼龙|n|cFFFFD200地区：|r风暴峭壁" },
    [60021] = { 32156, 1, 192, "被感染的始祖幼龙", "这个品种是专门为纪念在诺森德表现出非凡勇气和毅力的英雄而培育的。", 5, "|cFFFFD200传家宝|r" },
    [60024] = { 32157, 1, 128, "紫色始祖幼龙", "\"我无法停止思考如果泰坦带出另一只龙人会发生什么。\", – 阿莱克丝塔萨", 5, "|cFFFFD200成就：|r\"千奇百怪的漫长旅行\"|n|cFFFFD200分类：|r世界事件" },
    [60025] = { 32158, 1, 128, "白色幼龙", "\"连龙扩散控制协会的测试都无法确定这头白色幼龙是否属于任何已知的龙族。\", – 布琳妮", 5, "|cFFFFD200成就：|r\"骑兵队长\"|n|cFFFFD200分类：|r综合" },
    [60114] = { 32206, 2, 4, "装甲棕熊", "这只熊的迅捷因其沉重的盔甲而得到增强，并被肯瑞托的法师们迷住了。", 2, "|cFFFFD200商人出售：|r梅尔·弗兰希斯|n|cFFFFD200地区：|r达拉然|n|cFFFFD200价格：|r750|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [60116] = { 32207, 2, 4, "装甲棕熊", "“为了部落！”的呐喊声从这个装甲怪物的背后发出了尽可能令人生畏的声音，联盟全速在地面上滚动。", 2, "|cFFFFD200商人出售：|r梅尔·弗兰希斯|n|cFFFFD200地区：|r达拉然|n|cFFFFD200价格：|r750|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [60118] = { 32203, 2, 4, "黑色战熊", "击杀部落首领已经是成就，而这头可怕的凶兽，也将是一个伟大的战利品。", 5, "|cFFFFD200成就：|r\"为了联盟！\"|n|cFFFFD200分类：|rPvP" },
    [60119] = { 32205, 2, 4, "黑色战熊", "这种可怕的野兽可以通过将联盟指挥官的鲜血洒在你的武器上来获得。 但是免费杀死它们同样有趣。", 5, "|cFFFFD200成就：|r\"为了部落！\"|n|cFFFFD200分类：|rPvP" },
    [60120] = { 32208, 1, },
    [60136] = { 32212, 0, 64, "重型旅行猛犸象", "在艾泽拉斯最危险的贸易路线上旅行的商​​人已经准备好只相信这只巨大的猛犸象来保证他们的安全。", 2, "|cFFFFD200传家宝|r" },
    [60140] = { 32213, 0, 64, "重型旅行猛犸象", "在艾泽拉斯的贸易路线上遇到这种猛犸象是最常见的事情。 这种冷静、高大的野兽已成为前往异国和偏远贸易地区的商人的最爱。", 2, "|cFFFFD200传家宝|r" },
    [60424] = { 32286, 2, 4, "机械师的摩托车", "这个工程奇迹是艾泽拉斯上为两个骑手设计的少数摩托车之一。", 3, "|cFFFFD200专业技能：|r工程学" },
    [61229] = { 32335, 1, 132, "装甲雪色狮鹫", "\"当这些狮鹫攻击时，鲜血在它们的羽毛上几乎没有留下任何污渍。 太棒了，对吧？\" – 梅尔·弗兰希斯.", 2, "|cFFFFD200商人出售：|r梅尔·弗兰希斯|n|cFFFFD200地区：|r达拉然|n|cFFFFD200价格：|r2000|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [61230] = { 32336, 1, 132, "装甲蓝色驭风者", "\"驭风者不是诺森德的本地人，但他们对寒冷的适应却出奇的好。\", – 梅尔·弗兰希斯.", 2, "|cFFFFD200商人出售：|r梅尔·弗兰希斯|n|cFFFFD200地区：|r达拉然|n|cFFFFD200价格：|r2000|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
