    [61309] = { 33030, 1, 1024, "华丽的飞毯", "当您购买地毯时，您会得到一种坚固的浸渍冰织物，并用冰蜘蛛的真丝加固。 您也可以单独购买备用枕头。", 3, "|cFFFFD200专业技能：|r裁缝" },
    [61425] = { 32633, 2, 4, "旅行者的苔原猛犸象", "古代手稿描绘了商人骑着这些巨大的野兽穿越冰冷的苔原。 这些图像让科学家们推测猛犸象是最早被驯化的坐骑。", 2, "|cFFFFD200商人出售：|r梅尔·弗兰希斯|n|cFFFFD200地区：|r达拉然|n|cFFFFD200价格：|r20000|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
