    [61447] = { 32640, 2, 4, "旅行者的苔原猛犸象", "在寒冷的苔原旅行时，骑手和商人一致相信只有这些强壮而耐寒的猛犸象。", 2, "|cFFFFD200商人出售：|r梅尔·弗兰希斯|n|cFFFFD200地区：|r达拉然|n|cFFFFD200价格：|r20000|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [61451] = { 33029, 1, 1024, "飞毯", "跳上飞毯，翱翔在光芒四射、无限美丽的艾泽拉斯之上。", 3, "|cFFFFD200专业技能：|r裁缝" },
    [61465] = { 31862, 2, 4, "重型黑色猛犸战象", "这些猛犸象曾经携带着重甲巨人，因此它们可以轻松携带一些较小的骑手。", 0, "|cFFFFD200掉落： |r首领掉落|n|cFFFFD200地区：|r阿尔卡冯的宝库" },
    [61467] = { 31861, 2, 4, "重型黑色猛犸战象", "有时，一窝猛犸象生来比同龄人更强壮、更听话。 这样的人被称为 \"猛士\", 他们同时带着几个骑手去前线。", 0, "|cFFFFD200掉落： |r首领掉落|n|cFFFFD200地区：|r阿尔卡冯的宝库" },
    [61469] = { 31857, 2, 4, "重型冰雪猛犸象", "这些猛犸象在风暴峰的狭窄小径上安家，这就是为什么冰霜巨人委托它们同时运送货物和几名骑手的原因。", 2, "|cFFFFD200商人出售：|r李奥霍夫|n|cFFFFD200地区：|r风暴峭壁|n|cFFFFD200阵营：|r霍迪尔之子 - 崇拜|n|cFFFFD200价格：|r10 000|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [61470] = { 31858, 2, 4, "重型冰雪猛犸象", "由于强风和雪崩，不建议霍迪尔之子独自穿越雷霆峰。", 2, "|cFFFFD200商人出售：|r李奥霍夫|n|cFFFFD200地区：|r风暴峭壁|n|cFFFFD200阵营：|r霍迪尔之子 - 崇拜|n|cFFFFD200价格：|r10 000|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
