    [61996] = { 31239, 1, 132, "蓝色龙鹰", "龙鹰是世界上已知的最致命的掠食者之一。 他们能够从很远的地方发现猎物，冲上去，用爪子和喙把它吃掉。", 5, "|cFFFFD200成就：|r\"坐骑成堆\"|n|cFFFFD200分类：|r综合" },
    [61997] = { 32944, 1, 132, "红色龙鹰", "龙鹰是世界上已知的最致命的掠食者之一。 他们能够从很远的地方发现猎物，冲上去，用爪子和喙把它吃掉。", 5, "|cFFFFD200成就：|r\"坐骑成堆\"|n|cFFFFD200分类：|r综合" },
    [62048] = { 25064, 1, 192, "黑色龙鹰坐骑", "每年有一次暂时的分裂和奇怪的事情发生。 伊利达雷斥候和末日预言者意识到自己掉进了塔纳利斯时非常惊讶。", 0, "|cFFFFD200掉落： |r末日行者|n|cFFFFD200地区：|r影月谷" },
    [63232] = { 33297, 2, 0, "暴风城战马", "虽然这个品种是在暴风城培育出来的，但它对冰冠冰川的寒冷气候有着难以置信的抵抗力。", 2, "|cFFFFD200商人出售：|r下士亚瑟·弗卢|n|cFFFFD200地区：|r冰冠冰川|n|cFFFFD200价格：|r100|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [63635] = { 33299, 2, 0, "暗矛迅猛龙", "达卡莱人是第一个繁殖这些蜥蜴的人，但后来他们越过了诺森德的暗矛部落。", 2, "|cFFFFD200商人出售：|r萨玛巴|n|cFFFFD200地区：|r冰冠冰川|n|cFFFFD200价格：|r100|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [63636] = { 33408, 2, 0, "铁炉堡战羊", "一位蛮锤历史爱好者声称，该品种最初是由矮人移居丹莫罗时驯化的。", 2, "|cFFFFD200商人出售：|r德里克·斑须|n|cFFFFD200地区：|r冰冠冰川|n|cFFFFD200价格：|r100|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [63637] = { 33298, 2, 0, "达纳苏斯夜刃豹", "当暗夜精灵剑齿虎死去时，骑士传统上会保留它的一颗尖牙作为纪念品。", 2, "|cFFFFD200商人出售：|r鲁克·鹰爪|n|cFFFFD200地区：|r冰冠冰川|n|cFFFFD200价格：|r100|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [63638] = { 33301, 2, 0, "诺莫瑞根机械陆行鸟", "\"当你的鼻子流血时，你现在需要在某个地方。\", – 莉蕾·转轴.", 2, "|cFFFFD200商人出售：|r莉蕾·转轴|n|cFFFFD200地区：|r冰冠冰川|n|cFFFFD200价格：|r100|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [63639] = { 33416, 2, 0, "埃索达雷象", "Exodar Elekks 由 O'ros 神圣化，他是一个与维伦和他的追随者一起从德拉诺前往艾泽拉斯的纳鲁。", 2, "|cFFFFD200商人出售：|r艾蕾希|n|cFFFFD200地区：|r冰冠冰川|n|cFFFFD200价格：|r100|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [63640] = { 33409, 2, 0, "奥格瑞玛战狼", "有一段时间，兽人之间出现了一种表情 \"饿得像奥格瑞玛狼\"。它仍然偶尔被血精灵开玩笑地使用。", 2, "|cFFFFD200商人出售：|r弗蕾卡·血斧|n|cFFFFD200地区：|r冰冠冰川|n|cFFFFD200价格：|r100|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [63641] = { 33300, 2, 0, "雷霆崖科多兽", "这位阿尔法男性是漫游莫高雷平原的科多兽力量的化身。", 2, "|cFFFFD200商人出售：|r杜尔鲁·雷角|n|cFFFFD200地区：|r冰冠冰川|n|cFFFFD200价格：|r100|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [63642] = { 33418, 2, 0, "银月城陆行鸟", "\"我说我想要一些鸡肉。 然后我趴在地板上，这个生物试图品尝我的眼睛。\", – 波兹尔·布拉斯波.", 2, "|cFFFFD200商人出售：|r特雷利斯·晨日|n|cFFFFD200地区：|r冰冠冰川|n|cFFFFD200价格：|r100|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [63643] = { 33414, 2, 0, "被遗忘者战马", "当堕落的英雄变成不死生物时，同样的命运也会降临在他们的坐骑身上。", 2, "|cFFFFD200商人出售：|r伊莉莎·基连|n|cFFFFD200地区：|r冰冠冰川|n|cFFFFD200价格：|r100|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [63796] = { 33848, 1, 128, "米米尔隆的头部", "泰坦的玩具，吸引了古神尤格萨隆的东西。 这是目前艾泽拉斯最大的机械侏儒头颅。", 0, "|cFFFFD200掉落：|r 尤格-萨隆|n|cFFFFD200地区：|r 奥杜尔" },
    [63844] = { 33857, 1, 128, "银色角鹰兽", "银色角鹰兽将只允许最勇敢、在战斗中磨砺的勇士骑马。", 2, "|cFFFFD200商人出售：|r阵营军需官|n|cFFFFD200地区：|r银色锦标赛, 冰冠冰川|n|cFFFFD200价格：|r150|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t|n" },
    [63956] = { 33892, 1, 128, "铁箍始祖幼龙", "剑和矛都不会留下痕迹：两侧都覆盖着铁甲。", 5, "|cFFFFD200成就：|r\"奥杜尔团队的荣耀（25人）\"|n|cFFFFD200分类：|r地下城与团队副本" },
    [63963] = { 33904, 1, 128, "铁锈始祖幼龙", "\"当然，它不像其他的那样闪闪发光，但这个发现仍然很有价值。\", – 布莱恩·铜须", 5, "|cFFFFD200成就：|r\"奥杜尔团队的荣耀（10人）\"|n|cFFFFD200分类：|r地下城与团队副本" },
    [64656] = { 34154, 2, 64, "蓝色骷髅战马", "这是一个罕见的标本，因为大多数物种在斯坦索姆的瑞文戴尔男爵统治期间消失了。", 2, "|cFFFFD200传家宝|r" },
    [64657] = { 34155, 2, 0, "白色科多兽", "\"科多兽是我们人民的支柱。无论部落贸易、漫游还是战斗，强大的科多兽总是与我们同在。\" – 哈布·爪蹄", 2, "|cFFFFD200商人出售：|r哈布·爪蹄|n|cFFFFD200地区：|r莫高雷|n|cFFFFD200价格：|r1|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [64658] = { 356, 2, 0, "黑狼", "这只狼的嚎叫声传遍数公里。", 2, "|cFFFFD200商人出售：|r奥古纳罗|n|cFFFFD200地区：|r奥格瑞玛|n|cFFFFD200价格：|r1|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [64659] = { 34156, 2, 4, "毒皮暴掠龙", "你从一只有鳞的蜥蜴身上培育出这只掠食龙，莫维克会为你感到骄傲。 他还会很快让你清理蜥蜴在角落里所做的事情。", 1, "|cFFFFD200任务：|r飞速成长|n|cFFFFD200地区：|r安戈洛环形山" },
