    [64927] = { 34225, 1, 192, "致命角斗士的冰霜巨龙", "这些复活的毁灭使者不惧怕死亡，他们带来了死亡。", 2, "|cFFFFD200传家宝|r" },
    [64977] = { 34238, 2, 0, "黑色骷髅战马", "被遗忘者们相信，如果没有明确的目标，就连驮兽都会因无法安息而痛苦。", 2, "|cFFFFD200商人出售：|r撒迦利亚·普斯特|n|cFFFFD200地区：|r提瑞斯法林地|n|cFFFFD200价格：|r1|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
