    [65439] = { 34425, 1, 192, "狂怒角斗士的冰霜巨龙", "一些艾泽拉斯最强大的巨龙被召唤穿过生与死之间的帷幕，成为不死生物，毫无疑问地为他们的新主人服务。", 2, "|cFFFFD200传家宝|r" },
    [65637] = { 34551, 2, 0, "重型红色雷象", "在埃索达到达艾泽拉斯后，红色的小雷象诞生了，这在纳格兰以前从未见过。 一些牧场主认为，这种颜色是由袭击秘血岛的相同辐射引起的。", 2, "|cFFFFD200商人出售：|r艾蕾希|n|cFFFFD200地区：|r冰冠冰川|n|cFFFFD200价格：|r500|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t5|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [65638] = { 34550, 2, 0, "迅捷月刃豹", "这些强大的野兽受到许多暗夜精灵的崇敬，因为它们闪闪发光的皮肤象征着月亮女神艾露恩。", 2, "|cFFFFD200商人出售：|r鲁克·鹰爪|n|cFFFFD200地区：|r冰冠冰川|n|cFFFFD200价格：|r500|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t5|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [65639] = { 34556, 2, 0, "迅捷红色陆行鸟", "\"谁染的？ 啊，一只鸟！ 我们添加了相当多的红色\", – 特雷利斯·晨日", 2, "|cFFFFD200商人出售：|r特雷利斯·晨日|n|cFFFFD200地区：|r冰冠冰川|n|cFFFFD200价格：|r500|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t5|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [65640] = { 34557, 2, 0, "迅捷灰色战马", "一位在银色十字军最艰难的战斗中变得坚强的老兵。", 2, "|cFFFFD200商人出售：|r下士亚瑟·弗卢|n|cFFFFD200地区：|r冰冠冰川|n|cFFFFD200价格：|r500|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t5|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [65641] = { 34558, 2, 4, "大型金色科多兽", "这位阿尔法男性是漫游南贫瘠之地的科多兽力量的缩影。", 2, "|cFFFFD200商人出售：|r杜尔鲁·雷角|n|cFFFFD200地区：|r冰冠冰川|n|cFFFFD200价格：|r500|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t5|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
