-- Load localization only if it matches the client locale
-- spell id=（数据第一列）~
if GetLocale() ~= "zhCN" then
    return;
end

function ezCollections:GetPetInfo(spellID)
    local info = ezCollections.Pets[spellID];
    if info then
        return unpack(info, 1, 7);
    end
end

ezCollections.Pets =
{
    [4055] = { 2671, 9, 2, nil, "机械松鼠的逻辑准备中心告诉它，要多收集鼠和螺栓，以冬季取用。", 3, "|cFFFFD200专业技能：|r工程学" },
    [10673] = { 7385, 7, 2, nil, "冬妮·安萨妮娅计划在她临终时找到一只灰猫陪葬，聪明的冒险者此刻又能大显身手了。", 2, "|cFFFFD200商人出售：|r冬妮·安萨妮娅|n|cFFFFD200地区：|r艾尔文森林|n|cFFFFD200售价：|r40|TINTERFACE\\MONEYFRAME\\UI-SILVERICON.BLP:0|t" },
    [10674] = { 7384, 7, 2, nil, "冬妮·安萨妮娅邀请这些猫来她的午后茶会，但她没有提供它们最爱的饮品：敌人的眼泪。", 2, "|cFFFFD200商人出售：|r冬妮·安萨妮娅|n|cFFFFD200地区：|r艾尔文森林|n|cFFFFD200售价：|r40|TINTERFACE\\MONEYFRAME\\UI-SILVERICON.BLP:0|t" },
